import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './AuthPage.css'; // Assuming you have shared CSS for auth pages
import { FaEye, FaEyeSlash } from 'react-icons/fa'; // Importing Font Awesome icons
import * as faceapi from 'face-api.js';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State to toggle password visibility
  const [isFaceAuthEnabled, setIsFaceAuthEnabled] = useState(false); // State for face authentication
  const videoRef = useRef();
  const canvasRef = useRef();

  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = '/models';
      await faceapi.loadSsdMobilenetv1Model(MODEL_URL);
      await faceapi.loadFaceLandmarkModel(MODEL_URL);
      await faceapi.loadFaceRecognitionModel(MODEL_URL);
    };
    loadModels();
  }, []);

  const handleLogin = (e) => {
    e.preventDefault();
    const passwordRegex = /^(?=.*[A-Z]).{8,}$/;
    if (!passwordRegex.test(password)) {
      setError('Password must be at least 8 characters long and contain at least one uppercase letter.');
      return;
    }

    // Handle login logic here
    console.log('Logging in with', email, password);
    setError(''); // Clear any previous error messages
  };

  const handleGoogleLogin = () => {
    // Handle Google login logic here
    console.log('Logging in with Google');
  };

  const startVideo = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(stream => {
        videoRef.current.srcObject = stream;
      })
      .catch(err => console.error('Error accessing webcam: ', err));
  };

  const handleFaceAuthentication = async () => {
    const labeledDescriptors = await loadLabeledImages();
    const faceMatcher = new faceapi.FaceMatcher(labeledDescriptors, 0.6);
    videoRef.current.addEventListener('play', async () => {
      const canvas = faceapi.createCanvasFromMedia(videoRef.current);
      canvasRef.current.appendChild(canvas);
      const displaySize = { width: videoRef.current.width, height: videoRef.current.height };
      faceapi.matchDimensions(canvas, displaySize);
      setInterval(async () => {
        const detections = await faceapi.detectAllFaces(videoRef.current).withFaceLandmarks().withFaceDescriptors();
        const resizedDetections = faceapi.resizeResults(detections, displaySize);
        canvas.getContext('2d').clearRect(0, 0, canvas.width, canvas.height);
        const results = resizedDetections.map(d => faceMatcher.findBestMatch(d.descriptor));
        results.forEach((result, i) => {
          const box = resizedDetections[i].detection.box;
          const drawBox = new faceapi.draw.DrawBox(box, { label: result.toString() });
          drawBox.draw(canvas);
          if (result.label !== 'unknown') {
            // Successful face authentication logic here
            console.log('Face authenticated successfully');
          }
        });
      }, 100);
    });
  };

  const loadLabeledImages = () => {
    const labels = ['Person1', 'Person2']; // Replace with your labels
    return Promise.all(
      labels.map(async label => {
        const descriptions = [];
        for (let i = 1; i <= 3; i++) {
          const img = await faceapi.fetchImage(`/labeled_images/${label}/${i}.jpg`);
          const detections = await faceapi.detectSingleFace(img).withFaceLandmarks().withFaceDescriptor();
          descriptions.push(detections.descriptor);
        }
        return new faceapi.LabeledFaceDescriptors(label, descriptions);
      })
    );
  };

  return (
    <div className="auth-page container-fluid d-flex align-items-center justify-content-center">
      <div className="auth-container card shadow-lg p-4">
        <h1 className="text-center mb-4">Login</h1>
        <form onSubmit={handleLogin}>
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          <div className="form-group mb-3 position-relative">
            <input
              type="email"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group mb-3 position-relative">
            <input
              type={showPassword ? 'text' : 'password'}
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span
              className="position-absolute eye-icon"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
          <button type="submit" className="btn btn-primary btn-block">Login</button>
          <button type="button" className="btn btn-danger btn-block mt-3" onClick={handleGoogleLogin}>
            <i className="fab fa-google mr-2"></i>Login with Google
          </button>
          <button type="button" className="btn btn-success btn-block mt-3" onClick={() => {
            setIsFaceAuthEnabled(true);
            startVideo();
            handleFaceAuthentication();
          }}>
            <i className="fas fa-user-check mr-2"></i>Login with Face
          </button>
        </form>
        <p className="text-center mt-3">
          Don't have an account? <Link to="/register">Register</Link>
        </p>
      </div>
      {isFaceAuthEnabled && (
        <div className="face-auth">
          <video ref={videoRef} width="720" height="560" autoPlay muted></video>
          <div ref={canvasRef}></div>
        </div>
      )}
    </div>
  );
};

export default LoginPage;
